package Week2.day1;

public class car {
	public void applyBreak() {
		System.out.println("Apply break to Reduce the Speed of vehicle");
	}
	public void applyGear() {
		System.out.println("To Change the Speed of Vehicle");
	}
	public void switchOnAc() {
		System.out.println("Turn on the AC");
	}
	public void appplyAcclerate() {
		System.out.println("To Increase the Speed of Vehicle");
	}
	

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		car mycar = new car();
		mycar.applyBreak();
		mycar.applyGear();
		mycar.switchOnAc();
		mycar.appplyAcclerate();
		
		

	}

}
